mapper.select:
select row_number() over(order by adnm) serial,adnm,b.stcd,b.stnm,tm,ls1,ls2,ls3,ls4,ls5,z
from sq_LS_S
inner join ST_STBPRP_B b on sq_LS_S.STCD = b.STCD
inner join DJ_AD on b.ADDVCD = DJ_AD.ADCD
inner join DJ_EXP c on c.stcd = b.STCD
where tm between #{beginTime} and #{endTime}

	and b.addvcd IN

		#{adcd}





			and b.stcd IN

				#{stcdOrStnm}

			or b.stnm IN

				#{stcdOrStnm}



			and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')




	and c.lx IN

		#{systemTypes}



	and c.dx IN

		#{ly}

select row_number() over(order by adnm) serial,adnm,b.stcd,b.stnm,tm,ls1,ls2,ls3,ls4,ls5,z from ( select a.stcd,a.tm,ls1,ls2,ls3,ls4,ls5,z from sq_LS_S inner join (select stcd, Max(tm) tm from (select stcd,tm from sq_LS_S where tm <= #{time}) a group by stcd) a on sq_LS_S.STCD = a.STCD and sq_LS_S.TM = a.tm ) a inner join ST_STBPRP_B b on a.STCD = b.STCD inner join DJ_AD on b.ADDVCD = DJ_AD.ADCD inner join DJ_EXP c on c.stcd = b.STCD
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and c.dx IN
#{ly}
