mapper.select:
select st.stcd,st.stnm,st.name,adnm,isnull(numSix,0) numSix,isnull(numSeven,0) numSeven,isnull(numEight,0) numEight,isnull(numNine,0) numNine, (isnull(numSix,0)+isnull(numSeven,0)+isnull(numEight,0)+isnull(numNine,0)) as zong from (select b.* , e.name, ad.adnm from st_stbprp_b b INNER JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD inner join DJ_EXP c on b.STCD = c.stcd inner join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and STTP = 'P'
) st left join ( select sum(dyp) numSix, STCD from RP_PPTN_R where tm between #{beginTime1} and #{endTime1} and dyp > 0 group by STCD ) six on six.STCD = st.STCD left join ( select sum(dyp) numSeven, STCD from RP_PPTN_R where tm between #{beginTime2} and #{endTime2} and dyp > 0 group by STCD ) seven on seven.STCD = st.STCD left join ( select sum(dyp) numEight, STCD from RP_PPTN_R where tm between #{beginTime3} and #{endTime3} and dyp > 0 group by STCD ) eight on eight.STCD = st.STCD left join ( select sum(dyp) numNine, STCD from RP_PPTN_R where tm between #{beginTime4} and #{endTime4} and dyp > 0 group by STCD ) nine on nine.STCD = st.STCD left join (select stcd, drp from ${pptn} where tm=#{NowTime}) qt on st.STCD = qt.STCD ORDER BY ADNM

select st.stcd,st.stnm,st.name,adnm,isnull(numSix,0) numSix,isnull(numSeven,0) numSeven,isnull(numEight,0) numEight,isnull(numNine,0) numNine, (isnull(numSix,0)+isnull(numSeven,0)+isnull(numEight,0)+isnull(numNine,0)) as zong from (select b.* , e.name, ad.adnm from st_stbprp_b b INNER JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD inner join DJ_EXP c on b.STCD = c.stcd inner join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and STTP = 'P'
) st left join ( select MYMAVP numSix, STCD from RP_PDMMYAV_S where month = 6 group by STCD ) six on six.STCD = st.STCD left join ( select MYMAVP numSeven, STCD from RP_PDMMYAV_S where month = 7 group by STCD ) seven on seven.STCD = st.STCD left join ( select MYMAVP numEight, STCD from RP_PDMMYAV_S where month = 8 group by STCD ) eight on eight.STCD = st.STCD left join ( select MYMAVP numNine, STCD from RP_PDMMYAV_S where month = 9 group by STCD ) nine on nine.STCD = st.STCD left join (select stcd, drp from ${pptn} where tm=#{NowTime}) qt on st.STCD = qt.STCD ORDER BY ADNM
