mapper.select:
select st.stcd,st.stnm,st.name,adnm,isnull(num,0) num,isnull(qt.drp,0) drp from (select b.* , e.name, ad.adnm from st_stbprp_b b INNER JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD inner join DJ_EXP c on b.STCD = c.stcd inner join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and STTP = 'P'
) st left join (( select count(drp) numSix, STCD from RP_PPTN_R where tm between #{beginTime1} and #{endTime1} and dyp > 0 ) six on six.STCD = st.STCD left join ( select count(drp) numSeven, STCD from RP_PPTN_R where tm between #{beginTime2} and #{endTime2} and dyp > 0 ) seven on seven.STCD = st.STCD left join (select count(drp) numEight, STCD 6.02 7.01 7.02 8.01 from RP_PPTN_R where tm between #{beginTime3} and #{endTime3} and dyp > 0 ) eight on eight.STCD = st.STCD left join (select count(drp) numNine, STCD from RP_PPTN_R where tm between #{beginTime4} and #{endTime4} and dyp > 0 ) nine on nine.STCD = st.STCD group by STCD) pt on st.STCD = pt.STCD left join (select stcd, drp from ${pptn} where tm=#{NowTime}) qt on st.STCD = qt.STCD ORDER BY ADNM
