SELECT adnm, SUM (numSix) numSix, SUM (numSeven) numSeven, SUM (numEight) numEight, SUM (numNine) numNine, SUM (zong) zong FROM (select st.stcd,st.stnm,st.name,adnm,isnull(numSix,0) numSix,isnull(numSeven,0) numSeven,isnull(numEight,0) numEight,isnull(numNine,0) numNine, (isnull(numSix,0)+isnull(numSeven,0)+isnull(numEight,0)+isnull(numNine,0)) as zong from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD INNER JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD inner join DJ_EXP c on b.STCD = c.stcd inner join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and PFL = 1
) st left join ( select ACCP numSix, STCD from RP_PSTAT_R where IDTM = #{endTime1} and STTDRCD = 5 ) six on six.STCD = st.STCD left join ( select ACCP numSeven, STCD from RP_PSTAT_R where IDTM = #{endTime2} and STTDRCD = 5 ) seven on seven.STCD = st.STCD left join ( select ACCP numEight, STCD from RP_PSTAT_R where IDTM = #{endTime3} and STTDRCD = 5 ) eight on eight.STCD = st.STCD left join ( select ACCP numNine, STCD from RP_PSTAT_R where IDTM = #{endTime4} and STTDRCD = 5 ) nine on nine.STCD = st.STCD ) he GROUP BY ADNM

