select adnm, SUM (zong) zong FROM (select st.stcd,st.stnm,st.name,adnm,isnull(zong,0) zong from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD Left JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD Left join DJ_EXP c on b.STCD = c.stcd Left join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and PFL = 1
) st left join ( select sum(MYDAVP) zong, STCD from RP_PDDMYAV_S where
MNTH >= #{beginTime} and MNTH <= #{endTime}
MNTH = #{beginTime} and DAY >= #{endTime}
MNTH = #{beginTime} and DAY <= #{endTime}
MNTH = #{beginTime} and DAY > #{endTime} and DAY < #{isendTime}
and MYDAVP > 0 group by STCD) pt on st.STCD = pt.STCD ) he GROUP BY ADNM
