mapper.select:
select st.stcd,st.stnm,st.name,st.lgtd,st.lttd,tm,isnull(drp,0) drp,adnm from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD Left JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD Left join DJ_EXP c on b.STCD = c.stcd Left join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and PFL = 1
) st inner join ( select * from st_pptn_r m
m.tm>#{beginTime} and m.tm<=#{endTime} and m.drp is NOT NULL
)PP on ST.STCD=PP.STCD

select st.stcd,st.stnm,st.name,st.lgtd,st.lttd,tm,isnull(dyp,0) dyp,adnm from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and PFL = 1
) st LEFT join ( select * from ${pptn} m
m.tm=#{Time} and m.dyp is NOT NULL
)PP on ST.STCD=PP.STCD ORDER BY ADNM,st.STCD

select st.stcd,st.stnm,st.name,isnull(accp,0) accp,adnm,isnull(num,0) num from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD Left JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD Left join DJ_EXP c on b.STCD = c.stcd Left join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and PFL = 1
) st left join ( select * from ST_PSTAT_R m
m.IDTM=#{Time} and m.sttdrcd = '4'
)PP on ST.STCD=PP.STCD left join ( select count(*) num, STCD from ${pptn} where tm between #{beginTime} and #{Time} and dyp > 0 group by STCD) pt on st.STCD = pt.STCD ORDER BY ADNM,st.STCD

select st.stcd,st.stnm,st.name,isnull(accp,0) accp,adnm,isnull(num,0) num from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD Left JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD Left join DJ_EXP c on b.STCD = c.stcd Left join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and PFL = 1
) st left join ( select * from ST_PSTAT_R m
m.IDTM=#{Time} and m.sttdrcd = '5'
)PP on ST.STCD=PP.STCD left join ( select count(*) num, STCD from ${pptn} where tm between #{beginTime} and #{Time} and dyp > 0 group by STCD) pt on st.STCD = pt.STCD ORDER BY ADNM,st.STCD

select st.stcd,st.stnm,st.name,isnull(accp,0) accp,adnm,isnull(num,0) num from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD Left JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD Left join DJ_EXP c on b.STCD = c.stcd Left join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and PFL = 1
) st left join ( select * from ST_PSTAT_R m
m.IDTM=#{Time} and m.sttdrcd = '6'
)PP on ST.STCD=PP.STCD left join ( select count(*) num, STCD from ${pptn} where tm between #{beginTime} and #{Time} and dyp > 0 group by STCD) pt on st.STCD = pt.STCD ORDER BY ADNM,st.STCD

select st.stcd,st.stnm,st.name,adnm,isnull(num,0) num,isnull(qt.drp,0) drp from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD Left JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD Left join DJ_EXP c on b.STCD = c.stcd Left join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and PFL = 1
) st left join ( select sum(drp) num, STCD from ${pptn} where tm between #{beginTime} and #{endTime} group by STCD) pt on st.STCD = pt.STCD left join (select stcd, drp from ${pptn} where tm=#{NowTime}) qt on st.STCD = qt.STCD ORDER BY ADNM,st.STCD
