mapper.select:
select st.stcd,st.stnm,st.name,st.lgtd,st.lttd,tm,drp,adnm from (select b.* , e.name, ad.adnm from st_stbprp_b b INNER JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD inner join DJ_EXP c on b.STCD = c.stcd inner join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd = ${stcdOrStnm[0]} or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and STTP = 'P'
) st inner join ( select * from st_pptn_r m
m.tm>#{beginTime} and m.tm<=#{endTime} and m.drp is NOT NULL
)PP on ST.STCD=PP.STCD

select st.stcd,st.stnm,st.name,st.lgtd,st.lttd,tm,dyp,adnm from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd = ${stcdOrStnm[0]} or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and STTP = 'P'
) st LEFT join ( select * from st_pptn_r m
m.tm=#{Time} and m.dyp is NOT NULL
)PP on ST.STCD=PP.STCD ORDER BY ADNM

select st.stcd,st.stnm,st.name,accp,adnm,num from (select b.* , e.name, ad.adnm from st_stbprp_b b INNER JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD inner join DJ_EXP c on b.STCD = c.stcd inner join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd = ${stcdOrStnm[0]} or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and STTP = 'P'
) st left join ( select * from ST_PSTAT_R m
m.IDTM=#{Time} and m.sttdrcd = '4'
)PP on ST.STCD=PP.STCD left join ( select count(*) num, STCD from ST_PPTN_R where tm between #{beginTime} and #{Time} and dyp is NOT NULL group by STCD) pt on st.STCD = pt.STCD ORDER BY ADNM

select st.stcd,st.stnm,st.name,ACCP,adnm,num from (select b.* , e.name, ad.adnm from st_stbprp_b b INNER JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD inner join DJ_EXP c on b.STCD = c.stcd inner join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd = ${stcdOrStnm[0]} or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and STTP = 'P'
) st left join ( select * from ST_PSTAT_R m
m.IDTM=#{Time} and m.sttdrcd = '5'
)PP on ST.STCD=PP.STCD left join ( select count(*) num, STCD from ST_PPTN_R where tm between #{beginTime} and #{Time} and dyp is NOT NULL group by STCD) pt on st.STCD = pt.STCD ORDER BY ADNM

select st.stcd,st.stnm,st.name,ACCP,adnm,num from (select b.* , e.name, ad.adnm from st_stbprp_b b INNER JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD inner join DJ_EXP c on b.STCD = c.stcd inner join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd = ${stcdOrStnm[0]} or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and STTP = 'P'
) st left join ( select * from ST_PSTAT_R m
m.IDTM=#{Time} and m.sttdrcd = '6'
)PP on ST.STCD=PP.STCD left join ( select count(*) num, STCD from ST_PPTN_R where tm between #{beginTime} and #{Time} and dyp is NOT NULL group by STCD) pt on st.STCD = pt.STCD ORDER BY ADNM

select st.stcd,st.stnm,st.name,adnm,num,qt.drp from (select b.* , e.name, ad.adnm from st_stbprp_b b INNER JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD inner join DJ_EXP c on b.STCD = c.stcd inner join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd = ${stcdOrStnm[0]} or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and STTP = 'P'
) st left join ( select count(drp) num, STCD from ST_PPTN_R where tm between #{beginTime} and #{endTime} and dyp is NULL group by STCD) pt on st.STCD = pt.STCD left join (select stcd, drp from ST_PPTN_R where tm=#{NowTime}) qt on st.STCD = qt.STCD ORDER BY ADNM
