mapper.select:
select st.stcd,st.stnm,st.rvnm,adnm,isnull(AVQ,0) avQ from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and ZFL = 1 and STTP LIKE '%Z%'
) st LEFT JOIN ( select A.STCD,AVQ from RP_RVAV_R A
IDTM > #{beginTime} AND IDTM <= #{endTime} and A.STTDRCD=5
group by stcd,AVQ )PP on ST.STCD=PP.STCD

select st.stcd,st.stnm,st.rvnm,adnm,maxZ,maxZTime,maxQ,maxQTime from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and ZFL = 1 and STTP LIKE '%Z%'
) st LEFT JOIN ( select x.STCD,maxZ,maxZTime,maxQ,maxQTime from RP_RIVER_R x left join (select v.STCD,MAX (a.z) maxZ,max(tm) maxZTime from RP_RIVER_R v inner join (select stcd, max(z) z from RP_RIVER_R where tm > #{beginTime} and tm <= #{endTime} group by stcd) a on v.STCD = a.STCD and v.Z = a.z where tm > #{beginTime} and tm <= #{endTime} group by v.stcd) z on x.STCD = z.STCD left join( select v.STCD,MAX (b.q) maxQ,max(tm) maxQTime from RP_RIVER_R v inner join (select stcd, max(q) q from RP_RIVER_R where tm > #{beginTime} and tm <= #{endTime} group by stcd) b on v.STCD = b.STCD and v.q = b.q where tm > #{beginTime} and tm <= #{endTime} group by v.stcd) q on x.STCD = q.STCD group by x.STCD,maxZ,maxZTime,maxQ,maxQTime )PP on ST.STCD=PP.STCD

        select
        MAX(tm)
        from
        RP_RIVER_R
        where

            z=#{max}


            q=#{max}

        and
        stcd = #{stcd}
        and

				tm>=#{beginTime} and tm<=#{endTime}
			;
