mapper.select:
select st.stcd,st.stnm,z,tm,st.rvnm,adnm,q ,wptn=CASE PP.wptn when'4' then '落' when'5' then '平' when'6' then '涨' ELSE PP.wptn END from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
${benqu} and ZFL = 1 and STTP LIKE '%Z%'
) st LEFT JOIN ( select A.STCD, A.TM, z,msamt,q,A.wptn from ST_RIVER_R A INNER JOIN (select STCD, max(tm)tm from ST_RIVER_R
tm>#{beginTime} and tm<=#{endTime}
group by stcd) B on A.STCD = B.STCD and B.tm = A.TM )PP on ST.STCD=PP.STCD

select st.stcd,st.stnm,z,tm,st.rvnm,adnm,q ,wptn=CASE PP.wptn when'4' then '落' when'5' then '平' when'6' then '涨' ELSE PP.wptn END from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and c.dq = '31' and ZFL = 1 and STTP LIKE '%Z%' and c.jdb in (2,3)
) st LEFT JOIN ( select A.STCD, A.TM, z,msamt,q,A.wptn from RP_RIVER_R A INNER JOIN (select STCD, max(tm)tm from RP_RIVER_R
tm<=#{endTime}
group by stcd) B on A.STCD = B.STCD and B.tm = A.TM )PP on ST.STCD=PP.STCD

select st.stcd,st.stnm,z,tm,st.rvnm,adnm,q ,wptn=CASE PP.wptn when'4' then '落' when'5' then '平' when'6' then '涨' ELSE PP.wptn END from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and ZFL = 1 and STTP LIKE '%Z%' and c.dq = '32'
) st LEFT JOIN ( select A.STCD, A.TM, z,msamt,q,A.wptn from RP_RIVER_R A INNER JOIN (select STCD, max(tm)tm from RP_RIVER_R
tm<=#{endTime}
group by stcd) B on A.STCD = B.STCD and B.tm = A.TM )PP on ST.STCD=PP.STCD
