mapper.select:
select st.stcd,st.stnm,name,AVINQ,AVOTQ from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and c.dx IN
#{ly}
and sttp like '%R%' and ZFL='1' and c.jdb in (2,3)
) st LEFT JOIN ( select A.STCD, sum(INQ) AVINQ, sum(OTQ) AVOTQ from RP_RSVR_R A
TM > #{beginTime} AND TM <= #{endTime}
group by stcd )PP on ST.STCD=PP.STCD ORDER BY STCD

select st.stcd,st.stnm,name,rz,w from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and c.dx IN
#{ly}
and sttp like '%R%' and ZFL='1' and c.jdb in (2,3)
) st LEFT JOIN ( select A.STCD, AVG(rz) rz, AVG(w) w from RP_RSVR_R A
TM >= #{beginTime} and TM < #{endTime}
group by stcd )PP on ST.STCD=PP.STCD ORDER BY STCD

select st.adnm, st.rvnm, st.stcd,st.stnm,HNNM,isnull(w,0) w from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN RP_RSVRFCCH_B rc on rc.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and (b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and ((b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
)
and c.lx IN
#{systemTypes}
and RSVRTP IN (1,2,9)
and RSVRTP IN (3)
and RSVRTP IN (4,5)
and c.dx IN
#{ly}
and sttp like '%R%' and ZFL='1' and c.jdb in (2,3)
) st LEFT JOIN ( select A.STCD,AVG(W) w from RP_RSVR_R A
TM >= #{time1} and TM < #{time2}
group by stcd )PP on ST.STCD=PP.STCD

select st.stcd,st.stnm,HNNM,isnull(w,0) w from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN RP_RSVRFCCH_B rc on rc.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and (b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and ((b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
)
and c.lx IN
#{systemTypes}
and RSVRTP IN (1,2,9)
and RSVRTP IN (3)
and RSVRTP IN (4,5)
and c.dx IN
#{ly}
and sttp like '%R%' and ZFL='1' and c.jdb in (2,3)
) st LEFT JOIN ( select A.STCD,MYDAVW w from RP_RSVRMYAV_S A
MNTH = #{month} and DAY = #{day}
group by stcd,MYDAVW )PP on ST.STCD=PP.STCD

select st.stcd,st.stnm,st.RVNM,mrztm,mrz,mwtm,mw,minqtm,minq,motqtm,motq from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and c.dx IN
#{ly}
and sttp like '%R%' and ZFL='1' and c.jdb in (2,3)
) st LEFT JOIN ( select x.stcd,mrztm,mrz,mwtm,mw,minqtm,minq,motqtm,motq from RP_RSVR_R x left join( select r.stcd, max(tm) mrztm ,max(a.rz) mrz from RP_RSVR_R r inner join (select max(rz) rz,stcd from RP_RSVR_R where tm > #{beginTime} and tm <= #{endTime} group by STCD) a on r.stcd = a.stcd and r.RZ = a.rz where tm > #{beginTime} and tm <= #{endTime} group by r.stcd) rz on x.STCD = rz.STCD left join( select r.stcd, max(tm) mwtm ,max(b.w) mw from RP_RSVR_R r inner join (select max(w) w,stcd from RP_RSVR_R where tm > #{beginTime} and tm <= #{endTime} group by STCD) b on r.stcd = b.stcd and r.w = b.w where tm > #{beginTime} and tm <= #{endTime} group by r.stcd) w on x.STCD = w.STCD left join( select r.stcd, max(tm) minqtm ,max(c.inq) minq from RP_RSVR_R r inner join (select max(inq) inq,stcd from RP_RSVR_R where tm > #{beginTime} and tm <= #{endTime} group by STCD) c on r.stcd = c.stcd and r.INQ = c.inq where tm > #{beginTime} and tm <= #{endTime} group by r.stcd) inq on x.STCD = inq.STCD left join( select r.stcd, max(tm) motqtm ,max(d.otq) motq from RP_RSVR_R r inner join (select max(otq) otq,stcd from RP_RSVR_R where tm > #{beginTime} and tm <= #{endTime} group by STCD) d on r.stcd = d.stcd and r.OTQ = d.otq where tm > #{beginTime} and tm <= #{endTime} group by r.stcd) otq on x.STCD = otq.STCD group by x.stcd,mrztm,mrz,mwtm,mw,minqtm,minq,motqtm,motq )PP on ST.STCD=PP.STCD

select st.stcd,st.stnm,st.RVNM,mrz,mw,minq,motq from (select b.* , e.name, ad.adnm from st_stbprp_b b LEFT JOIN ST_STSMTASK_B s on b.STCD = s.STCD LEFT JOIN DJ_AD ad ON b.ADDVCD = ad.ADCD LEFT join DJ_EXP c on b.STCD = c.stcd LEFT join DJ_TYID e on c.lx = e.typeID
and b.addvcd IN
#{adcd}
and b.stcd IN
#{stcdOrStnm}
or b.stnm IN
#{stcdOrStnm}
and (b.stcd like '%${stcdOrStnm[0]}%' or b.stnm like '%${stcdOrStnm[0]}%')
and c.lx IN
#{systemTypes}
and c.dx IN
#{ly}
and sttp like '%R%' and ZFL='1' and c.jdb in (2,3)
) st LEFT join (select STCD, MAX(RZ) mrz, MAX(W) mw, MAX(INQ) minq, MAX(OTQ) motq from RP_RSVR_R group by STCD) RSVR on st.STCD = RSVR.STCD

select TM
from RP_RSVR_R
where STCD = #{stcd}
  and ${type} = #{typeValue}
  and TM between #{beginTime} and #{endTime};
